
/**
 * Process settings coming in via argv.
 *
 *  - title
 *  - module
 *  - port
 */

var args = process.argv
  , title = args[2] + '-worker'
  , path = args[3]
  , port = args[4];

process.title = title.replace(/\s+/g, '-') + '-worker';

var server = require(path);

server.on('close', function(){
  process.exit(0);
});

server.listen(port);
